package application;

public class App {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int[] numbers = {2,4,6,8};
		
		/*
		 * Shopping list
		 * 1. Tomatoes
		 * 2. Pasta
		 * 3. Onions
		 * 
		 * Array-Indexes are zero-based
		 */
		
		System.out.println(numbers[0]); // first element
		System.out.println(numbers[2]);
		System.out.println(numbers[3]);	// last element
		System.out.println(numbers[6]); // out of index

	}

}
