package application;

public class App {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int count = 0;
		
		System.out.println("First Loop:");
		do {
			System.out.println("Count: " + count);
			
		}while(count++ < 5);
				
		count = 0;
		
		System.out.println("Second Loop:");
		do {
			System.out.println("Count: " + count);
			
		}while(count++ < 3);
		
	}

}
