package application;

public class App {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int count = 0;
		System.out.println(count);
		
		count = count + 1;		
		System.out.println(count);
		
		count += 1;		
		System.out.println(count);
		
		count++; //Postfix		
		System.out.println(count);
		
		++count; //Prefix		
		System.out.println(count);
		
		//decrement		
		count -= 1;		
		System.out.println(count);
		
		count--; //Postfix		
		System.out.println(count);
		
		--count; //Prefix		
		System.out.println(count);
		
	

	}

}
